mapper.update:
UPDATE rooms

  number = #{number},
  capacity = #{capacity}

WHERE id = #{id}

mapper.select:
SELECT id, number, capacity,
  ( SELECT EXISTS
    (
      SELECT id FROM reservations
      WHERE room_id = rooms.id
      AND start_time <= #{searchTime}
      AND #{searchTime} <= end_time
    )
  ) AS currentUse
FROM rooms
WHERE study_cafe_id = #{studyCafeId}
