mapper.insert:
INSERT INTO bookmarks (user_id, study_cafe_id)
SELECT #{userId}, #{studyCafeId}
WHERE NOT EXISTS
(
  SELECT id FROM bookmarks
  WHERE user_id = #{userId} AND study_cafe_id = #{studyCafeId}
) FOR UPDATE

mapper.select:
SELECT EXISTS
(
  SELECT id FROM blacklist
  WHERE user_id = #{userId} AND study_cafe_id = #{studyCafeId}
)
